/**
 * This class will initiate the API.
 *
 * @author Tijs Verkoyen <tijs@sumocoders.be>
 */
class APIInit extends KernelLoader
{
	/**
	 * Current type
	 *
	 * @var	string
	 */
	private $type;

	/**
	 * @param string $type The type of init to load, possible values: backend, backend_ajax, backend_cronjob, backend_js
	 */
	public function initialize($type)
	{
		$allowedTypes = array('api');
		$type = (string) $type;

		// check if this is a valid type
		if(!in_array($type, $allowedTypes)) exit('Invalid init-type');

		// set type
		$this->type = $type;

		// set a default timezone if no one was set by PHP.ini
		if(ini_get('date.timezone') == '') date_default_timezone_set('Europe/Brussels');

		/**
		 * At first we enable the error reporting. Later on it will be disabled based on the
		 * value of SPOON_DEBUG, but for now it's required to see possible errors while trying
		 * to include the globals file(s).
		 */
		error_reporting(E_ALL | E_STRICT);
		ini_set('display_errors', 'On');

		$this->definePaths();
		$this->setDebugging();

		// get spoon
		require_once 'spoon/spoon.php';

		SpoonFilter::disableMagicQuotes();
		$this->initSession();
	}

	/**
	 * Define paths
	 */
	private function definePaths()
	{
		define('API_CORE_PATH', PATH_WWW . '/' . APPLICATION);
		define('BACKEND_PATH', PATH_WWW . '/backend');
		define('BACKEND_CACHE_PATH', BACKEND_PATH . '/cache');
		define('BACKEND_CORE_PATH', BACKEND_PATH . '/core');
		define('BACKEND_MODULES_PATH', BACKEND_PATH . '/modules');

		define('FRONTEND_PATH', PATH_WWW . '/frontend');
		define('FRONTEND_CACHE_PATH', FRONTEND_PATH . '/cache');
		define('FRONTEND_CORE_PATH', FRONTEND_PATH . '/core');
		define('FRONTEND_MODULES_PATH', FRONTEND_PATH . '/modules');
		define('FRONTEND_FILES_PATH', FRONTEND_PATH . '/files');
	}

	/**
	 * A custom error-handler so we can handle warnings about undefined labels
	 *
	 * @param int $errorNumber The level of the error raised, as an integer.
	 * @param string $errorString The error message, as a string.
	 * @return bool
	 */
	public static function errorHandler($errorNumber, $errorString)
	{
		$errorNumber = (int) $errorNumber;
		$errorString = (string) $errorString;

		// is this an undefined index?
		if(mb_substr_count($errorString, 'Undefined index:') > 0)
		{
			// cleanup
			$index = trim(str_replace('Undefined index:', '', $errorString));

			// get the type
			$type = mb_substr($index, 0, 3);

			// is the index locale?
			if(in_array($type, array('act', 'err', 'lbl', 'msg'))) echo '{$' . $index . '}';

			// return false, so the standard error handler isn't bypassed
			else return false;
		}

		// return false, so the standard error handler isn't bypassed
		else return false;
	}

	/**
	 * This method will be called by the Spoon Exceptionhandler and is specific for exceptions thrown in AJAX-actions
	 *
	 * @param object $exception The exception that was thrown.
	 * @param string $output The output that should be mailed.
	 */
	public static function exceptionAJAXHandler($exception, $output)
	{
		$output = (string) $output;

		// set headers
		SpoonHTTP::setHeaders('content-type: application/json');

		// create response array
		$response = array(
			'code' => ($exception->getCode() != 0) ? $exception->getCode() : 500,
			'message' => $exception->getMessage()
		);

		// output JSON to the browser
		echo json_encode($response);
		exit;
	}

	/**
	 * This method will be called by the Spoon Exceptionhandler
	 *
	 * @param object $exception The exception that was thrown.
	 * @param string $output The output that should be mailed.
	 */
	public static function exceptionHandler($exception, $output)
	{
		$output = (string) $output;

		// mail it?
		if(SPOON_DEBUG_EMAIL != '')
		{
			$headers = "MIME-Version: 1.0\n";
			$headers .= "Content-type: text/html; charset=iso-8859-15\n";
			$headers .= "X-Priority: 3\n";
			$headers .= "X-MSMail-Priority: Normal\n";
			$headers .= "X-Mailer: SpoonLibrary Webmail\n";
			$headers .= "From: Spoon Library <no-reply@spoon-library.com>\n";

			// send email
			@mail(SPOON_DEBUG_EMAIL, 'Exception Occurred (' . SITE_DOMAIN . ')', $output, $headers);
		}

		// build HTML for nice error
		$html = '<!doctype html>
				 <html lang="en">
				 <head>
					 <meta charset="utf-8">
					 <!--[if IE]><![endif]-->
					 <!--[if IE]><meta http-equiv="X-UA-Compatible" content="IE=edge,chrome=1"><![endif]-->
					 <title>Something is broken</title>
					 <meta name="robots" content="noindex, nofollow">
					 <meta name="author" content="SumoCoders">
					 <link rel="shortcut icon" href="/backend/favicon.ico">
					 <style>
						html,body,div,span,applet,object,iframe,h1,h2,h3,h4,h5,h6,p,blockquote,pre,a,abbr,acronym,address,big,cite,code,del,dfn,em,img,ins,kbd,q,s,samp,small,strike,strong,sub,sup,tt,var,b,u,i,center,dl,dt,dd,ol,ul,li,fieldset,form,label,legend,table,caption,tbody,tfoot,thead,tr,th,td,article,aside,canvas,details,embed,figure,figcaption,footer,header,hgroup,menu,nav,output,ruby,section,summary,time,mark,audio,video{margin:0;padding:0;border:0;font:inherit;font-size:100%;vertical-align:baseline}html{line-height:1}ol,ul{list-style:none}table{border-collapse:collapse;border-spacing:0}caption,th,td{text-align:left;font-weight:normal;vertical-align:middle}q,blockquote{quotes:none}q:before,q:after,blockquote:before,blockquote:after{content:"";content:none}a img{border:none}article,aside,details,figcaption,figure,footer,header,hgroup,menu,nav,section,summary{display:block}body{font-size:13px;line-height:1.5;font-family:Lucida Grande,Arial,sans-serif;color:#444;background-color:#b2cadd}.wrapper{margin:0 auto;width:940px;position:relative}#content{text-align:center;background-color:#ebf3f9;height:450px}#content .header{padding-top:70px;padding-bottom:20px;background:url("data:image/png;base64,") no-repeat center bottom}#content .header h1{padding-bottom:12px;color:#3a4044;font-size:20px;font-weight:700;line-height:1.2;font-family:Helvetica,Arial,sans-serif}#content .header p{color:#606e79;font-size:13px;padding:0 0 12px 0}#content a,#content a:link,#content a:visited{background:#bdbebf;border:1px solid #1a518c;border-top:1px solid #356dab;color:#fff;cursor:pointer;display:inline-block;font:400 12px/1.363 Arial,sans-serif;margin-top:20px;padding:5px 12px 4px;text-align:left;text-decoration:none;background-image:-webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, #488cd6), color-stop(100%, #296fba));background-image:-webkit-linear-gradient(top, #488cd6 0%,#296fba 100%);background-image:-moz-linear-gradient(top, #488cd6 0%,#296fba 100%);background-image:-o-linear-gradient(top, #488cd6 0%,#296fba 100%);background-image:linear-gradient(top, #488cd6 0%,#296fba 100%);-webkit-border-radius:4px;-moz-border-radius:4px;-ms-border-radius:4px;-o-border-radius:4px;border-radius:4px;text-shadow:rgba(41,96,167,0.6) 0 1px 0}#content a:hover,#content a:focus,#content a:link:hover,#content a:link:focus,#content a:visited:hover,#content a:visited:focus{background-image:-webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, #2c6cb3), color-stop(100%, #225a97));background-image:-webkit-linear-gradient(top, #2c6cb3 0%,#225a97 100%);background-image:-moz-linear-gradient(top, #2c6cb3 0%,#225a97 100%);background-image:-o-linear-gradient(top, #2c6cb3 0%,#225a97 100%);background-image:linear-gradient(top, #2c6cb3 0%,#225a97 100%)}#content a:active,#content a:link:active,#content a:visited:active{background:#A9D2E9;border:1px solid #7096ab;color:#313131;text-decoration:none}#content a .icon,#content a:link .icon,#content a:visited .icon{display:block;float:left;height:15px;width:15px;margin:0 8px 2px 0;background:url("data:image/png;base64,") no-repeat center transparent}#content #illustration{width:584px;height:410px;position:absolute;top:250px;left:200px;background:url("data:image/png;base64,") no-repeat transparent;padding-bottom:30px}
					 </style>
				</head>
				<body>
					<div id="content">
						<div class="wrapper">
							<div class="header">
								<h1>Something is broken</h1>
								<p>Looks like someone has been eating our servers. We are working on it!</p>
							</div>
							<a href="/" class="button"><span class="icon"></span>Go to the homepage</a>
							<div id="illustration"></div>
						</div>
					</div>
				</body>
				</html>';

		// output
		echo $html;
		exit;
	}

	/**
	 * This method will be called by the Spoon Exceptionhandler and is specific for exceptions
	 * thrown in JS-files parsed through PHP
	 *
	 * @param object $exception The exception that was thrown.
	 * @param string $output The output that should be mailed.
	 */
	public static function exceptionJSHandler($exception, $output)
	{
		$output = (string) $output;

		// set correct headers
		SpoonHTTP::setHeaders('content-type: application/javascript');

		// output exception
		echo '// ' . $exception->getMessage();
		exit;
	}

	/**
	 * Start session
	 */
	private function initSession()
	{
		SpoonSession::start();
	}

	/**
	 * Set debugging
	 */
	private function setDebugging()
	{
		// debugging enabled
		if(SPOON_DEBUG)
		{
			// set error reporting as high as possible
			error_reporting(E_ALL | E_STRICT);

			// show errors on the screen
			ini_set('display_errors', 'On');

			/*
			 * in debug mode notices are triggered when using non existing locale, so we use a custom
			 * errorhandler to cleanup the message
			 */
			set_error_handler(array('APIInit', 'errorHandler'));
		}

		// debugging disabled
		else
		{
			// set error reporting as low as possible
			error_reporting(0);

			// don't show error on the screen
			ini_set('display_errors', 'Off');

			switch($this->type)
			{
				case 'backend_ajax':
					Spoon::setExceptionCallback(__CLASS__ . '::exceptionAJAXHandler');
					break;

				case 'backend_js':
					Spoon::setExceptionCallback(__CLASS__ . '::exceptionJSHandler');
					break;

				default:
					Spoon::setExceptionCallback(__CLASS__ . '::exceptionHandler');
			}
		}
	}
}
